package datatype;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a =23;
		int b = 24;
		long c = 25;
		double d= 2.4;
		char e= 'A';
		String f = "welcome";
	
		System.out.println("byte data type is:"+a);
		System.out.println("int data type is:"+b);
		System.out.println("long data type is:"+c);
		System.out.println("double data type is:"+d);
		System.out.println("char data type is:"+e);
		System.out.println("string data type is:"+f);

	}

}
